-- ----------------------------
-- Table structure for cms_article
-- ----------------------------
DROP TABLE IF EXISTS `cms_article`;
CREATE TABLE `cms_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(200) DEFAULT NULL COMMENT '标题',
  `content` longtext COMMENT '文章内容',
  `picture` varchar(200) DEFAULT NULL COMMENT '标题图片',
  `channel_id` int(11) DEFAULT NULL COMMENT '所属栏目',
  `category_id` int(11) DEFAULT NULL COMMENT '所属分类',
  `user_id` int(11) DEFAULT NULL COMMENT '文章发布人',
  `hits` int(11) DEFAULT NULL,
  `hot` int(11) DEFAULT NULL,
  `status` int(11) DEFAULT NULL COMMENT '0,刚发布,1审核通过,-1 审核未通过',
  `deleted` int(11) DEFAULT NULL COMMENT '0:正常,1:删除',
  `created` datetime DEFAULT NULL,
  `updated` datetime DEFAULT NULL,
  `summary` text,
  `content_type` int(11) DEFAULT '0' COMMENT '文章类型: 1:html 2:image',
  `keywords` varchar(100) DEFAULT NULL,
  `original` varchar(100) DEFAULT NULL,
  `complain_num` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_CMS_ARTI_REFERENCE_CMS_USER` (`user_id`),
  KEY `FK_CMS_ARTI_REFERENCE_CMS_CHAN` (`channel_id`),
  KEY `FK_CMS_ARTI_REFERENCE_CMS_CATE` (`category_id`),
  CONSTRAINT `cms_article_ibfk_1` FOREIGN KEY (`category_id`) REFERENCES `cms_category` (`id`),
  CONSTRAINT `cms_article_ibfk_2` FOREIGN KEY (`channel_id`) REFERENCES `cms_channel` (`id`),
  CONSTRAINT `cms_article_ibfk_3` FOREIGN KEY (`user_id`) REFERENCES `cms_user` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=29 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of cms_article
-- ----------------------------
INSERT INTO `cms_article` VALUES ('12', 'S18赛季前瞻，对抗路有额外经验加成，野区即将迎来巨变！', '<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	前几天，\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	体验服迎来了一次比较大的更新，不仅上线了新英雄蒙犽，还对野区和对抗路的环境进行了众多的改动，应该会放在S18赛季初的版本更新中上线正式服。关于新英雄蒙犽的评测，我会放在以后给大家解析，今天，就先给各位讲解一下S18赛季对于地图资源的改动内容。<b>①对抗路</b>取消远程兵的生命回复效果，改为经验兵，经验兵额外提供65点经验<b>【看法】血包兵变成了经验兵，无疑会让对抗路的英雄等级提得更快，让一些比较吃四级的英雄变强不少，如花木兰，曹操，铠和李信这些，缓解了他们在四级前会被越塔强杀的压力。</b>\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://r.sinaimg.cn/large/article/c3b88c9cab3f3d63e1546b22012dd310\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	（我在体验服测试了一下，S18赛季的对抗路，正常发育下，1分45秒左右就能到四级了，基本上是一波兵提一级，足足比S17赛季快了半分多钟）与此同时，还会让对抗路的英雄有了更多的主动权，在前期的小规模团战中，可以发挥出更强的战斗力，配合队友抢下节奏的主动权。<br />\n<b>除此之外，由于血包兵的消失，也会让对抗路的对线期更为的激烈，会更考验玩家的基本功。</b>如果被单杀一次或者压回家，吃不了经验兵，肯定会被拉开巨大的等级差，导致线上崩盘。<br />\n<b>②中路</b><br />\n中路河道之灵生命调整至与边路河道之灵一致，但是经济和经验奖励和外网一致（远高于边路河道之灵）<b>【看法】降低中路河道之灵血量的改动，一方面是给中单更多的发育资源，另一方面则是鼓励野辅前期在中路打架，抢夺河道之灵。</b>这样一来，前期伤害高的中单明显有优势一些，可以趁机滚起更大的雪球，加快自己的装备成型速度。<br />\n<b>③先知主宰/主宰</b>先知主宰首次刷新时间点：第5分钟 → 第2分钟先知主宰基础生命：22000 → 12500先知主宰基础攻击：360 → 200先知主宰再次刷新间隔调整为4分钟先知主宰击杀全队获得50点经验和40点金币 → 击杀全队获得50点经验和25点金币主宰击杀全队获得200点经验和100金币 → 击杀全队获得200点经验和50金币【看法】先知主宰的刷新时间改为了2分钟，也即是说，会跟暴君一同出现，另外，先知主宰还下调了血量和攻击力，就是为了方便玩家击杀，鼓励玩家去打架去争夺。<b>如此一来，肯定会加快整个游戏的前期节奏，让打野有了更多的战略思路，特别是对抗路现在还有了经验兵，那打野配合好对抗路的队友，就能快速的rush掉先知主宰了。</b>\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://r.sinaimg.cn/large/article/cb1c789e97244674825521debe5d809d\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	（我在体验服测试了一下，5级的花木兰打2分钟的先知主宰大概只需要20秒左右，并保持较为健康的血量，加上打野估计10秒不到就能rush掉了）<br />\n与此同时，发育路被gank风险也会小了很多，射手可以比较安稳的发育，等到有了一件套之后，再出来打架了。<br />\n<b>④主宰先锋</b>主宰先锋基础生命：5000 → 3500 (在10分钟成长至与之前一致)主宰先锋基础攻击：200 → 150 (在10分钟成长至与之前一致)主宰先锋基础经验：145 → 30主宰先锋第10分钟成长到满经验：290 → 150 (约为一波兵线经验的50%)主宰先锋基础金币：60 → 120主宰先锋第10分钟成长到满金币：90 → 150<b>【看法】主宰先锋下调了前10分钟的生命，攻击力和经验值，提升了前10分钟的金币收益，就是为了平衡先知主宰的价值。</b>让主宰先锋对兵线和等级的压制效果更加明显，同时，还不至于把优势方的经济压得完全无法正常对抗。<b>⑤暴君</b>暴君击杀全队获得280点经验和60金币 → 击杀全队获得50点经验和100(每分钟额外增长5)金币暴君再次刷新间隔调整为4分钟【看法】暴君降低了击杀经验却又上调了击杀金币，并且这个击杀金币还会随着时间而提升，这也是为了区分先知主宰与暴君的性能，让先知主宰更适合滚等级差，暴君更适合滚经济差。<b>像一些比较想打野区入侵的阵容应该会更倾向于拿先知主宰多一些，一方面是为了处理兵线，另一方面则是为了提等级，让自己的野区作战能力更强一些，而偏发育的阵容则倾向于拿暴君来让自己的装备快速成型。</b>除此之外，还会让前期进攻的阵容更有优势，比如下路选个公孙离，李元芳或者马可波罗，中单再来个法刺，2分钟之后，中射辅去拿暴君，上野就去拿先知主宰，一起把双龙拿掉，瞬间把雪球滚起来。<br />\n<b>⑥黑暗暴君</b>暴君击杀全队获得200点经验和60金币 → 击杀全队获得75点经验和150金币【看法】黑暗暴君上调了金币收益和下调了经验加成，也是为了区分黑暴和主宰的性能，跟上面的意思差不多，不过到了10分钟后，这点经验和金币的收益差距已经显得没有那么重要了。<br />\n<b>⑦小型野怪</b>刷新频率：小怪刷新间隔 70秒 → 80秒【看法】小怪的刷新间隔增多，会让打野有了更多的时间去gank和做资源争夺，加整个前中期的节奏。<b>⑧中型野怪（腥红石像、蔚蓝石像）</b>Buff持续时间：70秒 → 80秒刷新频率：90秒 → 100秒&nbsp;【看法】红蓝buff的持续时间和刷新间隔增多，同样也是鼓励打野去gank和打架，掌控节奏的主动权。<br />\n<b>⑨野怪成长</b>金币收益：10分钟成长为基础的150% → 10分钟成长为基础的200%基础血量：10分钟成长为基础的200% → 10分钟成长为基础的250%基础攻击：10分钟成长为基础的125% → 10分钟成长为基础的150%【看法】野怪的血量，金币收益和攻击力的提升，一方面是为了平衡野怪刷新间隔的增加，让打野的经济得到补偿，同时，还会限制那些没有带打野刀的英雄争抢野怪，避免劣势方的野区瞬间被轮空。<b>⑩腥红石像Buff</b>每次普攻击对敌人造成30%减速持续2秒，并附带2段灼烧效果，每段造成20~48点伤害 →每次普攻击对敌人造成30%减速持续2秒，并附带2段灼烧效果，每段造成30~128点真实伤害，伤害效果对野怪会强化为60~256点<b>【看法】红buff的伤害得到全面的提升，不仅打人更疼了，也会让打野刷野效率更快，大幅度的提升了打野的跑线和gank的效率，让打野彻底成为了前中期的节奏带动者。</b>\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://r.sinaimg.cn/large/article/869eb71a6ecd0eb2532bcd662475b9a3\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	同时，也能提升一些前期伤害较低的普攻型英雄的战斗力，让他们也有能力去争夺资源，打开局势，当然，射手也会舒服很多，中后期伤害和发育速度应该也能加快不少。<br />\n<b>⑪风暴龙王</b>获得感电：对身边范围内的非友方单位自动进行雷击，每击对英雄造成4%最大生命真实伤害。获得雷电护佑：直接获得基于自身最大生命50%的护盾→获得感电：对身边范围内的非友方单位自动进行雷击，每击对英雄造成5%最大生命真实伤害，这个伤害效果对英雄以外的单位会增强为15%获得雷电护佑：每次脱战额外5秒后会都可获得基于自身最大生命30%的护盾，护盾在风暴觉醒buff持续时间结束后不会立即拔除<b>【看法】风暴龙王buff提升了伤害，清线速度和护盾的持续时间，也是为了加强风暴龙王决策比赛的能力，让优势方拿到手之后，更能上高地终结比赛。</b>不至于像S17赛季那样，一下子盾被打破之后，就很难把优势滚为胜势了。<br />\n<b>总结</b>整体看下来，我认为S18赛季的改动方向还是挺正确的，让在S17赛季里环境比较恶劣的打野，战坦边和中单都舒服了不少，特别是战坦边，不再是被遗弃的一路了，地位明显会得到不小的提升，决策比赛的能力也加强许多。<br />\n<b>除此之外，S18赛季还会是一个更快节奏的打架版本，会比现在更加考验玩家的支援意识和小规模团战的配合能力，想要在新环境下适者生存，肯定需要有更强的节奏感才行。</b><br />\n建议大家从现在开始，慢慢的去提升一下自己的支援能力，同时也要去练习一下那些打架强势的英雄，这样才能更好的迎接S18赛季。\n</p>', '5f530aa5-7fa7-4850-88da-df4f39ffb857.jpg', '8', '32', '153', '0', '1', '1', '1', '2019-12-17 19:37:23', '2019-12-17 19:37:23', null, '0', null, null, '0');
INSERT INTO `cms_article` VALUES ('13', '真闹心！买房被“强塞”个车位，车位太拥挤没法正常开车门', '<p>\n	<span style=\"font-size:16px;\">近日，有多位安徽阜阳市一小区业主向中国之声反映说，位于当地颍泉区的“海亮星城小区”停车位过于拥挤，甚至并行停放在车位之内的车辆都无法正常打开车门，部分车位还挡住了消防等安全设施。</span>\n</p>\n<p>\n	<span style=\"font-size:16px;\"><br />\n</span>\n</p>\n<p>\n	<br />\n<span style=\"font-size:16px;\"></span>\n</p>\n<p>\n	<span style=\"font-size:16px;\">此外，也有不少业主称，当初在租售车位时存在强行捆绑销售的情况。</span>\n</p>\n<p>\n	<span style=\"font-size:16px;\"><br />\n</span>\n</p>\n<p>\n	<span style=\"font-size:16px;\"></span><strong><span style=\"font-size:16px;\">那么，</span></strong><span style=\"font-size:16px;\"><strong>小区车位为何如此规划？</strong></span><strong><span style=\"font-size:16px;\">业主又该怎样维护自己的权利？</span></strong>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p style=\"text-align:center;\">\n	<strong><span style=\"color:#007AAA;\">必须支付12万元购买一个车位的使用权</span></strong>\n</p>\n<p style=\"text-align:center;\">\n	<strong><span style=\"color:#007AAA;\">才能参与选房</span></strong><strong><span style=\"font-size:16px;color:#007AAA;\"></span></strong>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-size:16px;\">资料显示，海亮星城小区是由海亮地产控股集团有限公司开发建成的，2017年7月正式开盘，并从2018年开始陆续交房，小区共有900多户。业主李先生（化名）告诉记者，起初选择购买该小区的房子主要是因为海亮地产在当地开发了多个楼盘，质量和声誉都不错。</span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<strong></strong><strong><span style=\"font-size:16px;\">张先生：</span></strong><span style=\"font-size:16px;\">“因为海亮当初在我们阜阳已经有了八九个地产项目，品牌也蛮大的，我们也比较信赖大公司，所以就购买了海亮地产（开发的小区）。”</span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-size:16px;\">不过在开盘当天，张先生却被告知必须支付12万元购买一个车位的使用权才能参与选房，张先生称，早就看中了这个小区，等开盘等了几个月，错过了其他楼盘，加上车位确实也是刚需，就交了这12万块钱。</span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<strong></strong><strong><span style=\"font-size:16px;\">张先生：</span></strong><span style=\"font-size:16px;\">“据我所知的话，（购买）洋房（的业主车位价格）基本上都是12万，然后（购买）高层（的业主车位价格）基本上都是9万。”</span>\n</p>\n<p>\n	<br />\n</p>\n<p style=\"text-align:center;\">\n	<img src=\"https://r.sinaimg.cn/large/tc/mmbiz_qpic_cn/6d468beab5344c4a9b54e4046eb12af7.jpeg\" />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-size:16px;\">也有业主反映称，起初该小区对外宣称送车位，直至交完定金选房时才得知必须捆绑购买车位的使用权，王女士（化名）就是其中一个。</span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<strong></strong><strong><span style=\"font-size:16px;\">王女士：</span></strong><span style=\"font-size:16px;\">“下午要开盘了，要选房了，然后才告诉我们要必须强制绑定车位，之前他们宣传的是说送车位的。”</span>\n</p>\n<p>\n	<br />\n</p>\n<p style=\"text-align:center;\">\n	<img src=\"https://r.sinaimg.cn/large/tc/mmbiz_qpic_cn/a2aaeb0db53f15dd9b0265e5ee636e7d.jpeg\" />\n</p>\n<p>\n	<br />\n</p>\n<p style=\"text-align:center;\">\n	<strong><span style=\"color:#007AAA;\">停车位拥挤，无法正常打开车门，</span></strong>\n</p>\n<p style=\"text-align:center;\">\n	<strong><span style=\"color:#007AAA;\">还挡住了消防等安全设施</span></strong>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-size:16px;\">花了大价钱购买车位的使用权，却没想到，车位现在成了业主们最头疼的话题。上个月底，停车位开始交付验收使用，走进停车场后，业主们发现停车位的划分非常的拥挤，紧挨着的两个停车位在车辆进入以后，之间的距离甚至不足以打开一扇车门。</span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<strong></strong><strong><span style=\"font-size:16px;\">张先生：</span></strong><span style=\"font-size:16px;\">“去调（车位的）划线之后，每个车位的实际的宽度就只有2.1（米）左右。</span>”\n</p>\n<p>\n	<br />\n</p>\n<p style=\"text-align:center;\">\n	<img src=\"https://r.sinaimg.cn/large/tc/mmbiz_qpic_cn/96e3c45238b2d3950d95f40957aeebd7.jpeg\" />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-size:16px;\">目前市面上大部分的家用小轿车宽度都在1米8左右，近来比较受车主青睐的SUV等车型更宽。根据国家《车库建筑设计规范》，微型汽车和小型汽车的车间横向净距应为0.6米，与柱间距为0.3米。而海亮星城目前划分的大部分车位，在停车以后两辆车之间的宽度显然达不到要求。</span>\n</p>\n<p>\n	<span style=\"font-size:16px;\"><br />\n</span>\n</p>\n<p>\n	<span style=\"font-size:16px;\">除了车位偏窄，还有业主反映，该停车场内的很多车位还挡住了消防等安全设施，留下了很多安全隐患。</span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<strong></strong><strong><span style=\"font-size:16px;\">张先生：</span></strong><span style=\"font-size:16px;\">“比较严重的就是堵着消防栓、消防通道，我们电梯下到地下室之后的通道都被堵起来，还有就是（部分车位还）堵着人（民）防（空）设施的，堵着人（民）防（空）设施的门等等。”</span>\n</p>\n<p>\n	<br />\n</p>\n<p style=\"text-align:center;\">\n	<img src=\"https://r.sinaimg.cn/large/tc/mmbiz_qpic_cn/f7ae1862e63fd72b4a69aefb5c3c8465.jpeg\" />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-size:16px;\">记者也联系了该小区的开发商海亮地产控股集团有限公司的相关负责人，该负责人表示，目前公司已经接到业主的投诉正在积极处理此事。</span>\n</p>\n<p>\n	<span style=\"font-size:16px;\"><br />\n</span>\n</p>\n<p style=\"text-align:center;\">\n	<strong><span style=\"color:#007AAA;\">律师：业主可</span></strong><strong><span style=\"color:#007AAA;\">向相关部门投诉维权</span></strong>\n</p>\n<p style=\"text-align:center;\">\n	<span style=\"font-size:16px;\"></span>\n</p>\n<p>\n	<span style=\"font-size:16px;\"><br />\n</span>\n</p>\n<p>\n	<span style=\"font-size:16px;\">北京中闻律师事务所律师李斌认为，开发商提供的车位违反了《车库建筑设计规范》的规定，业主可以选择解除当初签订的购买合同，要求退款，也可以要求重新划分车位。</span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<strong></strong><strong><span style=\"font-size:16px;\">李斌：</span></strong><span style=\"font-size:16px;\">“依照法律规定进行起诉是没有任何问题的，法律依据可以是《合同法》，也可以是双方签订的关于车库有偿使用的合同。因为双方的合同目的一定是停车场能够顺利的进行停放（车辆），而且作为业主可以顺利的上下车，并且不会发生剐蹭，不会影响他的正常出入。这是基本的合同条款，如果开发商违约，合同约定也是主张它承担违约责任的双方之间的依据。</span>”\n</p>\n<p>\n	<br />\n</p>\n<p style=\"text-align:center;\">\n	<img src=\"https://r.sinaimg.cn/large/tc/mmbiz_qpic_cn/f720079c3648f424c8c1f112a4909a93.jpeg\" />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-size:16px;\">李斌表示，如果小区停车场的确存在消防等安全隐患，且开发商采取拖延等办法迟迟不给业主解决问题，业主可以选择向公安机关或者其他涉及到的部门投诉。</span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<strong></strong><strong><span style=\"font-size:16px;\">李斌：</span></strong><span style=\"font-size:16px;\">“开发商如果拖延不予解决，作为业主可以选择向消费者协会投诉的方式进行维权，也可以选择向相关住建委进行投诉，因为住建委是开发商的行政主管机关，（可以）要求行政机关介入相关纠纷进行调解。”</span>\n</p>\n<p>\n	<br />\n</p>\n<p style=\"text-align:center;\">\n	<img src=\"https://r.sinaimg.cn/large/tc/mmbiz_qpic_cn/cb4d76dd74003ea7b2c261ca3d2ea787.jpeg\" />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-size:16px;\">针对此事，记者也联系了安徽省阜阳市颍泉区的相关政府部门的工作人员，截至发稿，没有得到回复。相关事件进展，中国之声将继续关注。</span>\n</p>\n<p>\n	<br />\n</p>\n<p style=\"text-align:center;\">\n	<img src=\"https://r.sinaimg.cn/large/tc/mmbiz_qpic_cn/e2454e864fce9a5d950a68bfaa56f358.jpeg\" />\n</p>\n<br />', '46090c93-4d1b-431f-bec3-852e0dde4640.jpg', '4', '13', '171', '0', '0', '1', '0', '2019-12-17 19:38:55', '2019-12-17 19:38:55', null, '0', null, null, '0');
INSERT INTO `cms_article` VALUES ('14', '关于割双眼皮宽度，避免双眼皮术后变内双！', '<h1 style=\"font-size:24px;color:#333333;font-family:Arial, &quot;\">\n	关于割双眼皮宽度，避免双眼皮术后变内双！&nbsp;\n</h1>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	双眼皮手术不仅仅是单纯的把单眼皮变成双眼皮，整形医生会根据受术者的职业特点与整个脸型，整体来设计双眼皮的宽度，从而让求美者不仅仅拥有双眼皮，更要拥有漂亮的双眼皮。\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://wx4.sinaimg.cn/large/8ecdf095gy1g9zub8rxsvj20di067402.jpg\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<br />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<b>双眼皮的宽度应该如何选择？</b>\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	艺龄周海院长介绍，不同人适合的双眼皮的宽度也会有所区别。\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://wx1.sinaimg.cn/large/8ecdf095gy1g9zubdu86yj20cl07ftc5.jpg\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<b>1、较窄双眼皮</b>\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	较窄的双眼皮宽度一般在4-5mm，一般这些人属于保守派女性，想要拥有明媚双眸，却又不想过于招摇，所以会选择较窄一些，不容易被人看出进行过双眼皮手术。\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<b>2、较宽双眼皮</b>\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	较宽的双眼皮宽度在1cm以上，这种适合脸型方正的人，可以无只限的炫耀自己的美。\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<b>3、适中双眼皮</b>\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	适中双眼皮宽度在7-8mm，这种宽度适合很多人，很多人乐于接受的。\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<b>4、全长一致双眼皮</b>\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	双眼皮的皱褶与睑缘的距离适中，并且两者长度保持一致。这种双眼皮让眼睛变得温柔、明快，适合眼裂细长的人群。\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<b>5、外宽内窄双眼皮</b>\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	双眼皮皱褶与睑缘的距离在睑缘的外1/3部较宽(大于7-8mm)，在内1/3部较窄(小于7-8mm)。这种适合于脸型长方。\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://wx3.sinaimg.cn/large/8ecdf095gy1g9zubny4x5j20f405xdi0.jpg\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<b>双眼皮手术后变内双的原因：</b>\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	双眼皮变内双有这几种情况：一种是手术时重睑宽度设计的过窄，术后肿胀没消失前，一般很难发现，但术后随着肿胀的消失就会出现双眼皮变内双的现象；另一种情况是埋线双眼皮缝线不牢靠造成的;还有一种情况是手术后，随着年龄的增长眼部皮肤松弛造成的双眼皮变内双。\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://wx2.sinaimg.cn/large/8ecdf095gy1g9zubv00crj20b305j0tm.jpg\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<b>双眼皮手术后预防内双怎么做？</b>\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	首先，要想保证双眼皮手术安全，术后恢复效果好，就得学会选择一家正规的整形医院做双眼皮。\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	双眼皮手术后预防内双怎么做？在进行双眼皮修复的时候重新调整双眼皮的弧度，高低，宽窄，深浅，手术过程当中尽量将组织复位固定，解决因为解剖层次不清晰造成的粘连，睑缘外翻，双眼皮过深过浅，双眼皮脱落消失等缺陷。\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://wx1.sinaimg.cn/large/8ecdf095gy1g9zuc68imnj20gq0g447c.jpg\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	双眼皮变内双矫正手术相对于第一次做双眼皮而言难度要大些，具体采用什么术式则需根据个人的实际情况而定\n</p>', null, '6', '24', '153', '0', '0', '1', '1', '2019-12-17 19:44:20', '2019-12-17 19:44:20', null, '0', null, null, '0');
INSERT INTO `cms_article` VALUES ('15', '邓亚萍吐槽:中国男篮是真不行', '<span style=\"color:#333333;font-family:Arial, &quot;font-size:14px;background-color:#FFFFFF;\">邓亚萍吐槽:中国男篮是真不行 王仕鹏:给您添堵了，在本期吐槽大会，前乒乓球奥运冠军邓亚萍一本正经道：“我们家儿子最喜欢的就是篮球，其次是足球。一个中国孩子喜欢这俩项目，多让人闹心啊！”她的发言引发了现场嘉宾和观众们的哄笑，游泳奥运冠军孙杨开心地鼓掌。王仕鹏笑着回应道：“给您添堵了！”</span><br />\n<img class=\"W_img_face\" src=\"https://img.t.sinajs.cn/t4/appstyle/expression/ext/normal/14/2018new_weiwu_org.png\" title=\"[威武]\" alt=\"[威武]\" style=\"width:18px;height:18px;\" /><span style=\"color:#333333;font-family:Arial, &quot;font-size:14px;background-color:#FFFFFF;\">接下来邓亚萍又单独调侃起了中国篮球：“本来篮球还行，结果呢——今年的男篮是真不行。不过，王仕鹏存在感很强倚老卖老，经常批评我们年轻运动员。我看了他的材料，不就是1983年的吗？就这样开始倚老卖老啦？拿过几个世界冠军？有多少块奥运金牌？是第几代‘大魔王’啊？”</span><br />\n<img class=\"W_img_face\" src=\"https://img.t.sinajs.cn/t4/appstyle/expression/ext/normal/14/2018new_weiwu_org.png\" title=\"[威武]\" alt=\"[威武]\" style=\"width:18px;height:18px;\" /><span style=\"color:#333333;font-family:Arial, &quot;font-size:14px;background-color:#FFFFFF;\">在毫不留情批评了中国男篮的成绩短板后，邓亚萍还是给王仕鹏挽回了些许“颜面”，追忆起2006年男篮世锦赛王仕鹏的惊世压哨三分球绝杀。但她最终还是没有“放过”王仕鹏，继续调侃道：“所以说运动员如果没有世界冠军，就只能拿一场胜利当自己的代表作了。”</span><br />\n<img class=\"W_img_face\" src=\"https://img.t.sinajs.cn/t4/appstyle/expression/ext/normal/14/2018new_weiwu_org.png\" title=\"[威武]\" alt=\"[威武]\" style=\"width:18px;height:18px;\" /><span style=\"color:#333333;font-family:Arial, &quot;font-size:14px;background-color:#FFFFFF;\">“我和孙杨家里，都挂满了（自己）和金牌、奖杯的合影；而王仕鹏家里呢，挂的是一个6秒钟的动图。”邓亚萍继续“刺激”着王仕鹏。被一再调侃的大鹏终于“忍不住”反驳道：“是5.8秒！”邓亚萍总结道：“看起来很在乎！”</span><br />\n<img class=\"W_img_face\" src=\"https://img.t.sinajs.cn/t4/appstyle/expression/ext/normal/14/2018new_weiwu_org.png\" title=\"[威武]\" alt=\"[威武]\" style=\"width:18px;height:18px;\" /><span style=\"color:#333333;font-family:Arial, &quot;font-size:14px;background-color:#FFFFFF;\">邓亚萍在节目中将“炮口”对准王仕鹏是事出有因的，就在本期节目的开始阶段，王仕鹏出言吐槽了邓亚萍。他表示：“我非常同情乒乓球队，你们的目标永远是冲出亚洲、外面没有世界了。”邓亚萍马上解释道：“对，全国冠军最难拿。”之后他又调侃起了邓亚萍的身高，并称其为“乒乓巨鳄”。</span><br />\n<img class=\"W_img_face\" src=\"https://img.t.sinajs.cn/t4/appstyle/expression/ext/normal/14/2018new_weiwu_org.png\" title=\"[威武]\" alt=\"[威武]\" style=\"width:18px;height:18px;\" /><span style=\"color:#333333;font-family:Arial, &quot;font-size:14px;background-color:#FFFFFF;\">之后，王仕鹏又将攻击目标转移向了孙杨，揭短孙杨在名人赛打篮球时不讲情面，对位女生时毫不留情，又不懂篮球的3秒违例、不讲规则。之后他又吐槽孙杨拿了世界冠军后变得骄傲起来了，并调侃说：“毕竟成绩太好了，我是他我也飘啊！”这番调侃逗得孙杨哈哈大笑。</span>', '5f71edcc-90f3-4e55-ad03-65ea95826817.jpg', '5', '19', '156', '0', '0', '1', '1', '2019-12-17 19:47:40', '2019-12-17 19:47:40', null, '0', null, null, '0');
INSERT INTO `cms_article` VALUES ('16', '徐峥导演的《宠爱》将成为下一匹黑马？', '<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	每年都会有许多电影，最火热的莫过于春节档、国庆档电影，而在2020年的元旦节，也有两部热门电影同期上线，一部是爱情家庭电影《亲爱的新年好》，一部是徐峥监制的爱情喜剧电影《宠爱》，都将在12月31日上映，与大家一同跨年。\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<br />\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://r.sinaimg.cn/large/article/94da27b789d21a264d1200ddacda6515\" alt=\"\" />\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://r.sinaimg.cn/large/article/d3a6f1ed757635b44ca721cd8cac174f\" alt=\"\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<br />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	从猫眼电影了解，徐峥监制的电影《宠爱》，有着超高的热度，6.9万人想看，可见热度非常高。小编后来了解到，这部剧超强阵容集结，由于和伟、吴磊、张子枫、钟汉良、杨子姗、陈伟霆、钟楚曦、檀健次、阚清子、郭麒麟、李兰迪主演，让人惊喜万分。\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<br />\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://r.sinaimg.cn/large/article/4d3e90e47f4a88e8b4cb14a3a3798618\" alt=\"\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<br />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	《宠爱》是一部爱情喜剧电影，该影片由6段温暖的故事组成，讲述了不同年龄段的人们，在宠物的陪伴和守护中，收获爱、发现爱，更重新学会爱的故事。超强阵容集结，让这部影片增加了不少热度，近日还发布了最新预告。\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<br />\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://r.sinaimg.cn/large/article/57a6608dd4c2a14d07532911a699badc\" alt=\"\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<br />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<br />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	从预告了解，杨子姗和钟汉良演绎一对情侣，男生因害怕女生讨厌宠物，所以一直隐瞒自己养猪的事情，当秘密被发现后，杨子姗化身“吃醋狂魔”，展开了一段和小猪的争宠之旅。那么问题来了，宠女友还是宠小猪？\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<br />\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://r.sinaimg.cn/large/article/725a6e2b156c4c79c9516f96e00bce24\" alt=\"\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<br />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	从海报了解，于和伟和李兰迪，演绎一对父女，他们有一只宠物猫，张子枫和吴磊演绎一对小情\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	侣，有一只宠物狗，钟楚曦和陈伟霆、阚清子和檀健次。那么亮点来了，郭麒麟只有一只狗，哈哈哈，好惨一男的，近期在《庆余年》也是笑点担当。\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<br />\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://r.sinaimg.cn/large/article/0bc39fcc4d67740d0afb936cd95aaa9c\" alt=\"\" />\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://r.sinaimg.cn/large/article/3e6861f4b4d36cc9a11ec239aedabff7\" alt=\"\" />\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://r.sinaimg.cn/large/article/ee3d87b1054f48a76187a6c02ef5f970\" alt=\"\" />\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	《宠爱》是由北京真乐道文化传播有限公司、北京海润影业股份有限公司、上海淘票票影视文化有限公司出品，由北京卓然影业有限公司、北京星魅力影视文化传媒集团有限公司、北京盛煜天桐文化传播有限公司、北京陆和文化传媒交流有限责任公司、新丽传媒集团有限公司联合出品，由上海淘票票影视文化有限公司发行。\n</p>\n<p align=\"justify\" style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<b><br />\n电影促进法的实施，允许个人或者企业参与电影投资，目前已经有很多来自五湖四海的大众出品人参与<b>《宠爱》</b>，</b><b>面向大众的福利电影。项目咨询微信：462923166</b>\n</p>', '77ed48cc-edc7-473e-aaea-1697fbda2d71.jpg', '6', '21', '171', '0', '0', '1', '0', '2019-12-17 20:01:36', '2019-12-17 20:01:36', null, '0', null, null, '0');
INSERT INTO `cms_article` VALUES ('17', '临颍民警辗转多地，追回被盗的5G手机一部', '<p style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	据魅力临颍消息，近日，在临颍县杜曲派出所所长刘要峰带领下，民警百里驱车赶赴郑州，成功追回被盗的一部5G手机。\n</p>\n<p class=\"picbox\" style=\"text-align:center;color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	<img src=\"https://r.sinaimg.cn/large/article/bfba0dea47e59f1cd1e940eed42fa686\" alt=\"\" />\n</p>\n<p style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	2019年12月10日上午，一名叫徐某的群众到杜曲派出所报警称，其手提包在某化妆品店被盗，包内有一部5G手机，价值5000元左右，包内还有800元现金及身份证、银行卡。\n</p>\n<p style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	抽死剥茧，锁定手机位置\n</p>\n<p style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	民警宋燕澎接到报警后及时对该案件立案调查，因被害人门前无监控录像，该案件一时无法着手。后民警经过细致工作，缜密侦查，得知该手机目前在商丘梁园区，并查出三个曾经插在手机的手机号，民警遂对每个机主进行联系，得知该手机是从郑州通讯大世界一摊位售出，后批发给商丘市梁园区一手机专卖店，专卖店老板又将手机销售给一男子，民警告知手机的三个经手人：“该手机系被盗手机，请立刻返还郑州通讯大世界老板李某处”。\n</p>\n<p style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	兵贵神速，及时追回手机\n</p>\n<p style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	经过不断努力，手机于12月11日上午已返还李某，民警在电话中告知李某要妥善保管好该手机，但李某电话中态度恶劣，拒不配合工作。民警宋燕澎及时将该情况向所长刘要峰汇报，刘要峰为防止被盗手机再次转手，当日亲自带队赶往郑州，后在郑州市公安局民警的大力配合下，找到郑州通讯大世界老板李某，及时将李某传唤至当地公安机关，经询问：李某称其将手机再次出售到郑州市西郊一手机专卖店内，民警驱车赶到西郊将该手机顺利追回。\n</p>\n<p style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	经调查：一名四十多岁的男子在12月10日下午将该手机以4000元的价格卖给他们，当时手机店内有高清监控，目前杜曲所民警对该案件在进一步的调查中。\n</p>\n<p style=\"color:#333333;font-family:Arial, &quot;font-size:16px;\">\n	民警为群众追回手机后，徐某激动地对民警说：“我没想到派出所的民警会这么重视，在百忙之中，才一天就帮我找回了被盗手机。”\n</p>', '4932a284-a866-4bc4-aea1-883c7f8235fc.jpg', '1', '1', '156', '0', '1', '1', '0', '2019-12-17 20:04:48', '2019-12-17 20:04:48', null, '0', null, null, '0');
INSERT INTO `cms_article` VALUES ('18', '为什么新ipad mini价格降的这么快？', '<div class=\"answer-item sticky-item\" style=\"margin:0px;padding:74px 0px 0px;color:#657180;font-family:&quot;background-color:#FFFFFF;\">\n	<div class=\"answer-text clean h_1200 fold\" style=\"margin:0px;padding:0px;font-size:16px;\">\n		<div class=\"answer-text-full rich-text\" style=\"margin:0px;padding:0px;\">\n			<p>\n				iPad mini 5刚刚发布的时候，很多网友都高呼“真香”。因为iPad mini 5搭载了苹果最强的A12处理器，而且屏幕够大，非常适合用来玩游戏，2999元的起始价格看起来也不是很贵。\n			</p>\n			<p>\n				但是iPad mini 5上市没多久，价格就一路走低，身在618期间出现了2488元的“神价格”，这一下子就比原价跌去了500块钱。那么是什么原因导致iPad mini 5的价格降得这么快呢？主要有以下几个原因：\n			</p>\n			<p>\n				<span style=\"font-weight:700;\">1、iPad使用场景太少。</span>很多网友把iPad买回来就是为了玩游戏、上网、看书、追剧。但是现在人们工作这么忙，有时候晚上回家还得加班，真正能够玩平板的时间非常少。而且iPad能做的事情，手机同样也可以做。所以很多网友的iPad在用过几次就放在家里“吃灰”了，有了这样的经验之后，这些网友再购买iPad mini 5之前就会仔细思考一下：“我究竟需不需要这款设备。”\n			</p>\n			<p>\n				<img src=\"http://p9.pstatp.com/large/1e06c0006d438af631d65\" />\n			</p>\n			<p>\n				<span style=\"font-weight:700;\">2、iPad专属应用太少。</span>现在AppStore中绝大多数APP都是针对手机屏幕的，适合在iPad上运行的APP太少，而且很多都是专业APP，也不适合一般用户使用。\n			</p>\n			<p>\n				<span style=\"font-weight:700;\">3、iPad mini屏幕太小。</span>iPad mini 5的7.9英寸屏幕是4:3的比例，很多视频、游戏都没办法全屏显示。当画面因为比例不匹配而被缩放之后，iPad mini 5的实际显示效果并不会比大屏手机强多少。所以如果用户已经有了6英寸以上的大屏幕手机，是没有必要为了追剧再买一台iPad mini 5的。\n			</p>\n			<p>\n				<img src=\"http://p1.pstatp.com/large/1e0620006d05b549ae5db\" />\n			</p>\n			<p>\n				同样的，一些图像处理、视频处理或者办公APP，也只适合在10英寸左右大大屏幕iPad上使用，有的还需要配合使用蓝牙键盘和手写笔。iPad mini 5虽然也支持键盘和笔，但7.9英寸的屏幕大大限制了它的办公性能。\n			</p>\n			<p>\n				<span style=\"font-weight:700;\">4、外观几乎没有提升。</span>iPad mini 5使用的仍然是上一代iPad mini 4的模具，也就是除了处理器和内存之外，其它零部件几乎没有变。面对这样“不走心”的产品愿意掏钱买的人自然也会很少。\n			</p>\n			<p>\n				<img src=\"http://p9.pstatp.com/large/1e0650006cd5a9a73ad70\" />\n			</p>\n			<p>\n				所以综合以上几点，iPad mini 5在买回来之后，似乎也只能用来上网、玩游戏、看书、追剧了，其它能够用到的地方实在太少。这样一来很多人就会觉得花2999元买一台iPad mini 5放在家里吃灰实在太不划算，而销量上不去产品自然也会跟着降价。最近又正好遇到618电商促销，iPad mini 5的快速降价并不令人感到意外。\n			</p>\n		</div>\n		<div class=\"answer-tool\" style=\"margin:15px 0px 19px;padding:2px 0px 0px;font-size:14px;color:#999999;\">\n			<div class=\"comment-tool\" style=\"margin:0px;padding:0px;\">\n				<a href=\"https://www.wukong.com/answer/6706368035789226247/?showComment=6706368035789226247\" target=\"_blank\" class=\"show-comment\"><span class=\"bui-icon icon-comments_small\" style=\"font-size:12px;line-height:1;color:#CACACA;font-family:tticons !important;\"></span>&nbsp;<span>145评论</span></a>\n			</div>\n		</div>\n	</div>\n</div>\n<div class=\"answer-item sticky-item\" style=\"margin:0px;padding:74px 0px 0px;color:#657180;font-family:&quot;background-color:#FFFFFF;\">\n	<div class=\"fake-cover\" style=\"margin:0px;padding:0px;\">\n	</div>\n	<div class=\"answer-user\" style=\"margin:0px 0px 14px;padding:0px;\">\n		<a href=\"https://www.wukong.com/user/?uid=77831636724\" target=\"_blank\" class=\"answer-user-avatar\"><img alt=\"\" src=\"http://p1.pstatp.com/thumb/66ae0000949c2fb37492\" style=\"width:40px;height:40px;\" /></a><a href=\"https://www.wukong.com/user/?uid=77831636724\" target=\"_blank\" class=\"answer-user-name\">LeoGo科技&nbsp;&nbsp;优质数码领域创作者</a><a class=\"answer-user-tag\">06-25 21:05</a>\n		<div class=\"answer-like\" style=\"margin:0px;padding:0px;color:#999999;font-size:14px;\">\n			<span class=\"bui-icon icon-thumbsup_line\" style=\"line-height:1;color:#CACACA;font-family:tticons !important;\"></span><span>11</span>赞\n		</div>\n		<div class=\"answer-dislike\" style=\"margin:0px;padding:0px;color:#999999;font-size:14px;\">\n			<span class=\"bui-icon icon-thumbsdown_line\" style=\"line-height:1;color:#CACACA;font-family:tticons !important;\"></span>踩\n		</div>\n	</div>\n	<div class=\"answer-text clean h_1200 fold\" style=\"margin:0px;padding:0px;font-size:16px;\">\n		<div class=\"answer-text-full rich-text\" style=\"margin:0px;padding:0px;\">\n			<blockquote style=\"color:#A8A8A8;background:#F0F0F0;\">\n				感谢您的阅读！\n			</blockquote>\n			<p>\n				我也非常纳闷：价格这么低的ipad mini 5，为什么就没有市场呢？难道它有什么“隐疾”？\n			</p>\n			<p>\n				如今，最便宜的A12处理器机型就是iPad mini 5，相比iPhone XR，iPad mini5真的是白菜价！可是，为什么我们就不愿意购买iPad mini5呢？我们分析有如下原因。<br />\n<img src=\"http://p1.pstatp.com/large/1e06b0005f1e290bc3a33\" />\n			</p>\n			<p>\n				<span style=\"line-height:28px;\">原因一：更偏向于使用手机。</span>大部分用户对于pad是没有太多的需求的，现在手机几乎能够满足所有的需求，看电视，听音乐等等，应用场景非常多。而ipad的功能基本上被手机代替，更为主要的是我们的手机现在越来越大，有的手机甚至已经达到了7.12英寸，携带更为方便。\n			</p>\n			<p>\n				<span style=\"line-height:28px;\">原因二：老款iPad耐用。</span>很多iPad的用户它们的老款iPad仍然可以使用，而且很多购买iPad的用户只是为了看电影听音乐，老款的iPad仍然性能优势非常突出，无需更换。老款iPad的它们本身的质量效果太突出了，反而对新款的mini5造成了影响。\n			</p>\n			<p>\n				<span style=\"line-height:28px;\">原因三：iPad实用性不高。</span>虽然iPad号称具有生产力的效果能够应用在各种场景，比如说画图等等。但是大部分购买ipad的用户其实就是为了看电影听音乐或者是玩游戏，它的实用性相对较局限，并不是因为iPad本身局限，而是因为用户对于使用有局限。<br />\n<img src=\"http://p3.pstatp.com/large/1e0620006d77d440e72a2\" />\n			</p>\n			<p>\n				<span style=\"line-height:28px;\">原因四：尺寸尴尬。</span>Mini5它的尺寸为7.9英寸，这个尺寸在我看来是非常尴尬的，因为说它大它也不算大，说它小它也不算小，对于需要使用iPad的人，可能它的尺寸过小，可能会考虑iPad pro等大尺寸；而对于想携带方便的人，可能手机是他们的选择。\n			</p>\n			<p>\n				<span style=\"line-height:28px;\">在这几种原因之下，我们在选购iPadmini5的时候可能会有犹豫，即使它的性能优势非常的强劲，而且价格也非常的优惠，但是我们会问自己到底购买它有什么作用？这几种情况导致我们并不愿意去考虑这款机型。</span>\n			</p>\n			<p>\n				销量不佳，让苹果只能够通过降价手段来吸引用户。\n			</p>\n		</div>\n		<div class=\"answer-tool\" style=\"margin:15px 0px 19px;padding:2px 0px 0px;font-size:14px;color:#999999;\">\n			<div class=\"comment-tool\" style=\"margin:0px;padding:0px;\">\n				<a href=\"https://www.wukong.com/answer/6706453671678312711/?showComment=6706453671678312711\" target=\"_blank\" class=\"show-comment\"><span class=\"bui-icon icon-comments_small\" style=\"font-size:12px;line-height:1;color:#CACACA;font-family:tticons !important;\"></span>&nbsp;<span>4评论</span></a>\n			</div>\n		</div>\n	</div>\n</div>\n<div class=\"answer-item sticky-item\" style=\"margin:0px;padding:74px 0px 0px;color:#657180;font-family:&quot;background-color:#FFFFFF;\">\n	<div class=\"fake-cover\" style=\"margin:0px;padding:0px;\">\n	</div>\n	<div class=\"answer-user\" style=\"margin:0px 0px 14px;padding:0px;\">\n		<a href=\"https://www.wukong.com/user/?uid=1323425760\" target=\"_blank\" class=\"answer-user-avatar\"><img alt=\"\" src=\"http://sf3-ttcdn-tos.pstatp.com/img/mosaic-legacy/2e56f00005e7e7644c1e8~120x256.image\" style=\"width:40px;height:40px;\" /></a><a href=\"https://www.wukong.com/user/?uid=1323425760\" target=\"_blank\" class=\"answer-user-name\">IT小超&nbsp;&nbsp;科技领域创作者</a><a class=\"answer-user-tag\">11-20 09:52</a>\n		<div class=\"answer-like\" style=\"margin:0px;padding:0px;color:#999999;font-size:14px;\">\n			<span class=\"bui-icon icon-thumbsup_line\" style=\"line-height:1;color:#CACACA;font-family:tticons !important;\"></span><span>3</span>赞\n		</div>\n		<div class=\"answer-dislike\" style=\"margin:0px;padding:0px;color:#999999;font-size:14px;\">\n			<span class=\"bui-icon icon-thumbsdown_line\" style=\"line-height:1;color:#CACACA;font-family:tticons !important;\"></span>踩\n		</div>\n	</div>\n	<div class=\"answer-text clean h_1200 fold\" style=\"margin:0px;padding:0px;font-size:16px;\">\n		<div class=\"answer-text-full rich-text\" style=\"margin:0px;padding:0px;\">\n			<p>\n				第一点、主要是老款IPAD几乎不会坏，更新换代比较慢\n			</p>\n			<p>\n				第二、现在手机屏幕已经很大了，5.5寸基本玩游戏，看视频足够了，最主要的是携带方便，出门带两台设备真的很麻烦。\n			</p>\n			<p>\n				第三、IPAD还无法完全取代电脑，完成办公需求。\n			</p>\n			<p>\n				<img src=\"http://p1.pstatp.com/large/2dd480004f88c2ae372e1\" />\n			</p>\n		</div>\n		<div class=\"answer-tool\" style=\"margin:15px 0px 19px;padding:2px 0px 0px;font-size:14px;color:#999999;\">\n			<div class=\"comment-tool\" style=\"margin:0px;padding:0px;\">\n				<a href=\"https://www.wukong.com/answer/6761200809985179911/?showComment=6761200809985179911\" target=\"_blank\" class=\"show-comment\"><span class=\"bui-icon icon-comments_small\" style=\"font-size:12px;line-height:1;color:#CACACA;font-family:tticons !important;\"></span>&nbsp;<span>评论</span></a>\n			</div>\n		</div>\n	</div>\n</div>\n<div class=\"answer-item sticky-item\" style=\"margin:0px;padding:74px 0px 0px;color:#657180;font-family:&quot;background-color:#FFFFFF;\">\n	<div class=\"fake-cover\" style=\"margin:0px;padding:0px;\">\n	</div>\n	<div class=\"answer-user\" style=\"margin:0px 0px 14px;padding:0px;\">\n		<a href=\"https://www.wukong.com/user/?uid=22381513711\" target=\"_blank\" class=\"answer-user-avatar\"><img alt=\"\" src=\"http://sf6-ttcdn-tos.pstatp.com/img/mosaic-legacy/241f0000e493081ed6785~120x256.image\" style=\"width:40px;height:40px;\" /></a><a href=\"https://www.wukong.com/user/?uid=22381513711\" target=\"_blank\" class=\"answer-user-name\">游戏人超超&nbsp;&nbsp;北京市眉州东坡酒楼餐饮部职员 游戏领域创作者</a><a class=\"answer-user-tag\">11-16 21:11</a>\n		<div class=\"answer-like\" style=\"margin:0px;padding:0px;color:#999999;font-size:14px;\">\n			<span class=\"bui-icon icon-thumbsup_line\" style=\"line-height:1;color:#CACACA;font-family:tticons !important;\"></span><span>0</span>赞\n		</div>\n		<div class=\"answer-dislike\" style=\"margin:0px;padding:0px;color:#999999;font-size:14px;\">\n			<span class=\"bui-icon icon-thumbsdown_line\" style=\"line-height:1;color:#CACACA;font-family:tticons !important;\"></span>踩\n		</div>\n	</div>\n	<div class=\"answer-text clean h_1200 fold\" style=\"margin:0px;padding:0px;font-size:16px;\">\n		<div class=\"answer-text-full rich-text\" style=\"margin:0px;padding:0px;\">\n			<p>\n				iPad mini 5刚刚发布的时候，很多网友都高呼“真香”。因为iPad mini 5搭载了苹果最强的A12处理器，而且屏幕够大，非常适合用来玩游戏，2999元的起始价格看起来也不是很贵。\n			</p>\n			<p>\n				但是iPad mini 5上市没多久，价格就一路走低，身在618期间出现了2488元的“神价格”，这一下子就比原价跌去了500块钱。那么是什么原因导致iPad mini 5的价格降得这么快呢？主要有以下几个原因：\n			</p>\n			<p>\n				1、iPad使用场景太少。很多网友把iPad买回来就是为了玩游戏、上网、看书、追剧。但是现在人们工作这么忙，有时候晚上回家还得加班，真正能够玩平板的时间非常少。而且iPad能做的事情，手机同样也可以做。所以很多网友的iPad在用过几次就放在家里“吃灰”了，有了这样的经验之后，这些网友再购买iPad mini 5之前就会仔细思考一下：“我究竟需不需要这款设备。”\n			</p>\n			<p>\n				2、iPad专属应用太少。现在AppStore中绝大多数APP都是针对手机屏幕的，适合在iPad上运行的APP太少，而且很多都是专业APP，也不适合一般用户使用。\n			</p>\n			<p>\n				3、iPad mini屏幕太小。iPad mini 5的7.9英寸屏幕是4:3的比例，很多视频、游戏都没办法全屏显示。当画面因为比例不匹配而被缩放之后，iPad mini 5的实际显示效果并不会比大屏手机强多少。所以如果用户已经有了6英寸以上的大屏幕手机，是没有必要为了追剧再买一台iPad mini 5的。\n			</p>\n			<p>\n				同样的，一些图像处理、视频处理或者办公APP，也只适合在10英寸左右大大屏幕iPad上使用，有的还需要配合使用蓝牙键盘和手写笔。iPad mini 5虽然也支持键盘和笔，但7.9英寸的屏幕大大限制了它的办公性能。\n			</p>\n			<p>\n				4、外观几乎没有提升。iPad mini 5使用的仍然是上一代iPad mini 4的模具，也就是除了处理器和内存之外，其它零部件几乎没有变。面对这样“不走心”的产品愿意掏钱买的人自然也会很少。\n			</p>\n			<p>\n				所以综合以上几点，iPad mini 5在买回来之后，似乎也只能用来上网、玩游戏、看书、追剧了，其它能够用到的地方实在太少。这样一来很多人就会觉得花2999元买一台iPad mini 5放在家里吃灰实在太不划算，而销量上不去产品自然也会跟着降价。最近又正好遇到618电商促销，iPad mini 5的快速降价并不令人感到意外。\n			</p>\n<br />\n<img src=\"http://p1.pstatp.com/large/tos-cn-i-0022/0bf1acb60da44feca4376d596751a949\" /><br />\n<img src=\"http://p9.pstatp.com/large/tos-cn-i-0022/c07ee1fe9bfd421585144e17f3c8fb55\" /><br />\n<img src=\"http://p1.pstatp.com/large/tos-cn-i-0022/cc345ce3e66145caadd031de815e9a41\" />\n		</div>\n		<div class=\"answer-tool\" style=\"margin:15px 0px 19px;padding:2px 0px 0px;font-size:14px;color:#999999;\">\n			<div class=\"comment-tool\" style=\"margin:0px;padding:0px;\">\n				<a href=\"https://www.wukong.com/answer/6759891473459577091/?showComment=6759891473459577091\" target=\"_blank\" class=\"show-comment\"><span class=\"bui-icon icon-comments_small\" style=\"font-size:12px;line-height:1;color:#CACACA;font-family:tticons !important;\"></span></a>\n			</div>\n		</div>\n	</div>\n</div>', 'cdcc8e66-2a27-496f-acb0-033fb8268d53.jfif', '1', '2', '170', '0', '0', '1', '0', '2019-12-17 20:31:41', '2019-12-17 20:31:41', null, '0', null, null, '1');
INSERT INTO `cms_article` VALUES ('20', '就今日！两场爆冷输球+两场大逆转，威少惹上麻烦，湖人太幸福', '<p style=\"color:#222222;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\n	就今日，联盟又迎一个疯狂之夜，两场爆冷输球+两场大逆转，悬念十足让人是直呼精彩，东西部部分球队都涉及其中，打的是相当的激烈！东部强队雄鹿与热火都爆冷输球，雷霆与红队则上演大逆转！\n</p>\n<div class=\"pgc-img\" style=\"margin:0px;padding:0px;font-size:16px;color:#222222;font-family:&quot;background-color:#FFFFFF;\">\n	<img src=\"http://p1.pstatp.com/large/pgc-image/21156b7cd8af4ea2872cb42641990f62\" alt=\"就今日！两场爆冷输球+两场大逆转，威少惹上麻烦，湖人太幸福\" />\n</div>\n<p style=\"color:#222222;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\n	雄鹿今日对阵缺少东契奇的独行侠队，在第四节还剩4分多钟情况下，字母哥当时砍了42分14个篮板被换下，不看比赛以为雄鹿大胜拿下了比赛，其实不然，独行侠当时已领先了两位数。全场比赛，字母哥天神下凡轰下48分14篮板4助攻，无奈队友不给力，最终败给了独行侠，东契奇激动地发推庆祝\n</p>\n<p style=\"color:#222222;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\n	除雄鹿队外，热火在巴特勒的带领下也爆冷输球，灰熊全民皆兵，全场压着热火打\n</p>\n<div class=\"pgc-img\" style=\"margin:0px;padding:0px;font-size:16px;color:#222222;font-family:&quot;background-color:#FFFFFF;\">\n	<img src=\"http://p3.pstatp.com/large/pgc-image/867be88124254826887b2df8f1642978\" alt=\"就今日！两场爆冷输球+两场大逆转，威少惹上麻烦，湖人太幸福\" />\n</div>\n<p style=\"color:#222222;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\n	除了以上这两场爆冷输球外，今日还有两场大逆转。马刺今日面对马赛克队多点开花，一度将分差拉大到25分，下半场哈登与威少率队开始发力，第三节打出30-17，第四节26-18，最终，他们上演大逆转力克马刺。\n</p>\n<p style=\"color:#222222;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\n	雷霆面对公牛，公牛一度将分差拉大到26分，不过保罗下半场找回节奏，突然爆发打的公牛是毫无招架之力。第三节雷霆单节33-22，第四节27-16，保罗末节狂飙5记三分独砍19分导演大逆转，这么一来，雷霆又成西部第八了，不是逼着志在摆烂的高层去交易他么\n</p>\n<div class=\"pgc-img\" style=\"margin:0px;padding:0px;font-size:16px;color:#222222;font-family:&quot;background-color:#FFFFFF;\">\n	<img src=\"http://p3.pstatp.com/large/pgc-image/3a7e559769af4c6293849682b8c7e3c5\" alt=\"就今日！两场爆冷输球+两场大逆转，威少惹上麻烦，湖人太幸福\" />\n</div>\n<p style=\"color:#222222;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\n	值得一提的是，在我们关注比赛同时，威少却惹上麻烦了。一位爵士球迷向法院起诉爵士和威少。他的律师要求得到6800万美元的赔偿，同时为他的女友Jennifer Huff要求得到3200万美元赔偿。值得一提的是，这位爵士球迷在2019年3月份的比赛中，对威少使用种族歧视词语，遭到爵士队永久禁赛。\n</p>\n<p style=\"color:#222222;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\n	面对如此，笔者只想说他们是真敢想，挺威少\n</p>\n<div class=\"pgc-img\" style=\"margin:0px;padding:0px;font-size:16px;color:#222222;font-family:&quot;background-color:#FFFFFF;\">\n	<img src=\"http://p9.pstatp.com/large/pgc-image/1f13c4b36f3045f4961762a787b691f4\" alt=\"就今日！两场爆冷输球+两场大逆转，威少惹上麻烦，湖人太幸福\" />\n</div>\n<p style=\"color:#222222;font-family:&quot;font-size:16px;background-color:#FFFFFF;\">\n	威少惹上麻烦了，而湖人则太幸福。因为雄鹿队输球，他们坐收渔翁之利，凭借着24胜3负战绩成为联盟第一！北京时间12月20日，湖人将会对阵雄鹿，字母哥放话了，直言不想连续输掉两场比赛，那么对此，你看好哪队呢\n</p>', '0d094409-5ca7-4f9d-bb82-7fd58ab82d4f.jfif', '5', '17', '170', '0', '1', '1', '0', '2019-12-17 20:36:34', '2019-12-17 20:36:34', null, '0', null, null, '1');
INSERT INTO `cms_article` VALUES ('21', '好抽的外烟品牌推荐', '<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">香烟作为一种特殊的商品，也有明显的地域之分，一些香烟品牌在当地很是畅销，却在外地基本见不到。除此之外，香烟还有国产香烟和外烟之分。所谓外烟就是指从国外进口的香烟，常见的有万宝路、爱喜、黑魔鬼以及马坝、kent、登喜路等知名品牌。很多烟民朋友对外烟青睐有加，出境旅游或出差都喜欢在机场免税店或当地买上一些作为烟粮，犒劳自己挑剔的嘴。为了便于烟友们挑选外烟</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">什么外烟品牌好抽</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 渠道❤ 危性ai148357其实这个完全看自己的喜好，看个人更喜欢原味抑或清香型、还是浓香型等等。目前国内常见的外烟品牌多以混合型为主，烟劲适中，普遍比较清淡。但非要说哪个牌子最好抽，这就不好说了，因为基本上各个品牌除了主打某一味型之外，还有其他品种推出。就笔者身边的烟友情况来说，混合型的万宝路(红万、白万、绿万等)，焦油含量较低的kent香烟(薄荷、爆珠等)以及黑魔鬼、seven stars等等都比较受欢迎。如果是刚开始尝试外烟的话，个人比较推荐万宝路和seven stars，既容易买到，烟味也不冲，香气醇和。</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">外烟品牌价格一览</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">系列&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 焦油含量&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">万宝路(红中免)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 12mg&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">万宝路(白中免)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8mg&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">万宝路(绿港版)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8mg&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">万宝路(绿中免)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8mg&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">健牌(薄荷炫)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4mg&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">健牌(浩白)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1mg&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">健牌(中免爆珠)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 6mg&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">555(金锐中免)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8mg&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">555(国际蓝尚)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 9mg&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">七星(灰免税)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ——&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">柔和七星(超醇)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4mg&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br />\n<br />\n<span style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">七星(原味中免)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 14mg&nbsp; </span>', 'af878293-1d9f-4ae7-991b-27631796d13e.jpg', '3', '10', '153', '0', '0', '-1', '0', '2019-12-18 09:01:06', '2019-12-18 09:01:06', null, '0', null, null, '0');
INSERT INTO `cms_article` VALUES ('22', '基金投顾大变局', '<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	在基金行业，有一个非常有意思的悖论：\n</p>\n<blockquote style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	当牛市来临时，投资者的买基热情高涨，行情会“非理性过热”，基金公司为了业绩，会不得不发很多新基金、建仓，又由于是“追涨”，表现很难好过市场行情，一旦市场开始下跌，这些基金的表现也会“一泻千里”；\n</blockquote>\n<blockquote style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	在熊市时，市场处于底部区域，交易不振，基金公司想要抄底建仓，又会面临无投资者认购/申购的窘境，于是又错过最佳建仓时机。而正确的买基方式恰恰是熊市建仓，规避过热投资。\n</blockquote>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	这个悖论最后的结果是——投资者与基金公司经常站在了投资的“对立面”。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	不过，这一悖论随着“基金投顾”业务的开展，或许将不复存在。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">1</span>\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	12月14日，证监会发布了第二批获得“公开募集证券投资基金投资顾问业务试点”的机构，分别是腾安基金、蚂蚁基金以及珠海盈米基金，共三家独立的基金销售机构。再加上此前10月份获得试点资格的嘉实、华夏、易方达、南方、中欧钱滚滚五家基金公司，具有公募基金投顾业务资格的机构已有八家。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	有的人可能会疑惑，投资顾问不是非常常见么？例如银行的理财顾问、券商的证券投资顾问、各种资产管理公司的投资顾问等等。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	看似一样的投资顾问，概念却大不相同。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	银行和券商的投资顾问是持证上岗，需要取得职业证书，如AFP、证券投资咨询业务（投资顾问、分析师）、CFP、RFP等，销售方式多是卖方产品导向，是将目前已有产品匹配到合适的投资者。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	基金投顾则不一样，作为一种新的资管类牌照，基金投顾的业务模式由“产品销售导向”转为了“财富管理增值导向”。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	传统的基金销售是卖方投顾，基金公司只要将基金卖出即可，赚取的是基金的申购费、赎回费、管理费等费用，基金公司的利益是将基金规模扩大，赚取的是“费用”。与投资者想要收益的利益目标，并不完全一致。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	新的基金投顾业务是买方投顾，强调为客户提供基金理财规划服务，而不是靠一味地做大销售规模来实现发展。收费模式从收取申购、赎回等费用，调整为按资产保有规模、收益情况等收取顾问费用，使得销售机构与投资者利益保持一致。销售机构又可以反过来进一步推进基金管理人提供更好的产品和服务，形成投资者、投资顾问、基金管理人的多方共赢，促使整个行业的良性循环。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	同时，买方投顾的模式对于基金公司而言同样有利。基金公司不用再完全依赖于渠道揽客，渠道成本会显著下降。为了吸引客户，申赎费和管理费都会降低，转为投顾服务费，投资者的成本也并不会因为投顾费用而显著上升。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	这种转变促使基金公司、中介销售等机构，将“卖出去就好”的卖方思维，转变为“投资者一直在我这里投资、增值”的买方思维，帮助投资者实现资产的保值和增值。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	那么，既然“基金投顾”业务看起来是一种无比美好的业务模式，那又为何如今才开始试点推广呢？\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">2</span>\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	从全球各个发达国家的发展历史看，经济发展带来的财富积累，会使社会大众的资产管理需求快速增加，进而带来资产规划、财富管理咨询等业务的爆发。美国、英国、日本、澳大利亚等国家莫不如是。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	我国同样如此，城镇居民家庭的人均可支配收入不断提升，同时，大资管行业整体规模也在快速增长（见图1），截止2018年末，整个大资管行业规模达到124万亿，过去五年平均复合增长率超过30%。可以说，收入提升必然带动资产管理业务的快速发展。\n</p>\n<p class=\"ql-align-center\" style=\"font-size:16px;text-align:center;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<img src=\"http://5b0988e595225.cdn.sohucs.com/images/20191217/396e352750e24aa3a1b05998eae552cb.png\" style=\"height:auto;\" />\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	除了可支配收入快速增长的驱动，资产管理方式的变革，也加速了公募基金业务模式的转变。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">第一，房子“只涨不跌”的时代过去了，市场需要新的资产管理方式。</span>在刚刚过去的房地产黄金时代，买房即可使资产增值。而基金不确定的波动收益，与投资房子相比，毫无竞争力。但是当房子不再是“必涨”的时候，公众自然需要新的财富管理方式，基金自然是最重要的方式之一。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">第二，资管新规发布后，资管领域的格局在逐渐发生变化。</span>银行理财不再刚性兑付，全市场的无风险收益率（没有任何风险时，投资获得的收益率）不断降低，投资者想要投资基金，但面对难以捉摸的资本市场，以及6000多只公募基金产品，很难做出准确的判断。投资者需要专业的投资解决方案，针对不同客户群体需求，针对性提供投顾服务。基金投资顾问就是机构提供解决方案的一种形式。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">第三，从资管行业的发展进程上看，货币基金引导投资者将储蓄逐渐变为低风险的基金，完成了资产的保值，互联网理财开启了公众的理财意识。</span>接下来，公众从资产保值升级为资产增值，需要在投资领域选择更专业的机构、更专业的服务，投资顾问正是关键。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">第四，资管行业的变革还体现在金融业的不断对外开放。</span>今年以来，我国金融对外开放的步伐逐渐加快，未来还会有越来越多的国际金融机构进入国内市场，投资顾问这种成熟的财富管理模式势必会引入国内，先行试点也是为以后更好地监管投顾业务打下良好的基础。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">最后，从国外投顾发展历史看，这种独立的服务模式能够基于中立的立场，为投资者提供综合的顾问式服务，成为基金投资中的主流服务者。</span>投资顾问通过其专业服务，追求不同资产组合产生的专业价值，规避基金购买中的逆向选择。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	以美国为例，截止2018年底，全部基金管理主体中，81%为独立的基金投资顾问，管理着70%的基金资产（见图2）。其它国家如澳大利亚的投顾管理基金资产超过50%，中国香港投顾管理规模则大约占30%。\n</p>\n<p class=\"ql-align-center\" style=\"font-size:16px;text-align:center;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<img src=\"http://5b0988e595225.cdn.sohucs.com/images/20191217/ad21f9d396af4227aef8abd7c612811d.png\" style=\"height:auto;\" />\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	随着资管市场的发展，资管行业从以产品为核心进化到以投顾为核心是必然趋势。从这个方面来讲，发展投资顾问是行业所需，也是投资者、产品发行方、中介机构的共同需求。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">3</span>\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	不管是对投资者还是机构，基金投顾模式都是一种比传统销售模式更优的选择，也被看做是基金行业的2.0时代的到来，但这种理论上多方共赢的模式，其发展仍然面临诸多挑战。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">第一，公众愿意为投顾服务付费么？</span>从根本上说，投顾服务是帮助客户做资产配置计划，从而达到长期投资、稳健增值的目的。这种模式在美国非常流行，但是目前，国内普通投资者对于收费的投顾服务还没有习惯。长期以来所形成的“零顾问费，依靠产品成交取得收入”的模式，是销售方已经习惯的逻辑，而投顾依靠的是专业的投资知识，取得投资者的信任，进而做出资产配置计划。这又恰恰是我国目前资管行业面临的人才困境，如果在专业服务上不能够让投资者信任，就不用提后续的服务了。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">第二，FoF没做成的事，投顾能做好么？</span>事实上，在基金投顾试点之前，已经有一种和投顾模式相同的“配置基金的基金”，即FoF，从实践结果来看，由于受到资产配置种类、资产质量等限制，被寄予厚望的FoF发展并不尽如人意，目前FoF规模尚不足100亿。在FoF中体现出的产品同质化、大类资产不足、缺乏做空机制等问题都将在投顾业务中一一体现。如何通过个性化的服务，走出和FoF的差异化道路，将是基金投顾的重要命题。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">第三，投资顾问能够真正客观公正么？</span>投资顾问的核心竞争力在于100%的客观公正，这样才能在所有的产品中选取最适合投资者的组合。但是，持有“投资顾问”试点牌照的机构，又都是基金公司的销售子公司及第三方销售机构，对于投顾而言，要为自己的客户考虑推荐基金，那么究竟是配置自家基金还是其他公司基金？如果面临自家基金出现风险性事件时，又如何给投资者提出客观公正的建议？如何防范投顾中的道德风险与利益输送？这些问题如何在制度上进行保证，仍旧需要监管与行业共同探索。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	<span style=\"font-weight:700;\">第四，智能投顾会迎来新的发展机遇么？</span>在投资顾问业务中，投资顾问的核心竞争力在于为投资者配置资产的服务能力，并收取投顾费用，这也是投资者所付出的一部分重要成本。因此，多数资产管理公司都在积极探索智能投顾的道路，国外有Wealthfront、Betterment、Vanguard等典型机构，国内如招商银行的摩羯智投、苏宁金融的苏宁智投等，在探索智能投顾的道路上都已经做出了成熟的系统和产品。如果基金投顾业务能够快速、顺利的开展，与其相同属性且更具价格优势的智能投顾同样会迎来快速发展，并对基金投顾产生冲击。如何平衡两者的发展，是行业发展所面临的新挑战。\n</p>\n<p style=\"font-size:16px;color:#191919;font-family:&quot;background-color:#FFFFFF;\">\n	不过，不管是对机构有利，还是对行业有利，基金投顾真正想要快速发展，最需要的是对投资者有利，也只有这样，投顾机构才能真正成为资产管理的未来趋势，并成长为公众资产管理的中流砥柱。\n</p>', 'f5396460-a377-4cdf-95f9-c194181c7047.jpeg', '2', '5', '156', '0', '0', '1', '0', '2019-12-18 09:38:20', '2019-12-18 09:38:20', null, '0', null, null, '3');
INSERT INTO `cms_article` VALUES ('23', '操场埋尸案一审宣判：主犯杜少平被判死刑', '<span style=\"color:#333333;font-family:Arial, &quot;font-size:17px;background-color:#FFFFFF;\">12月17日至18日，湖南省怀化市中级人民法院一审对被告人杜少平等人故意杀人案及其恶势力犯罪集团案件公开审理当庭宣判。被告人杜少平犯故意杀人等罪，数罪并罚，决定执行死刑，剥夺政治权利终身。被告人罗光忠犯故意杀人罪，判处死刑，缓期2年执行，剥夺政治权利终身。其他12名被告人分别被判处1年-8年不等有期徒刑。</span>', '37c4b37c-45ef-4556-abd7-57c3c5bda50e.jpg', '1', '2', '170', '0', '1', '1', '0', '2019-12-18 15:06:04', '2019-12-18 15:06:04', null, '0', null, null, '2');
INSERT INTO `cms_article` VALUES ('24', '考研倒计时2天！高校为备考学生送500份暖心蛋挞', '<div class=\"WB_editor_iframe_word\" style=\"margin:0px 0px 40px;padding:0px;font-size:17px;color:#333333;font-family:Arial, &quot;background-color:#FFFFFF;\">\n	<div style=\"margin:0px;padding:0px;\">\n		<div style=\"margin:0px;padding:0px;\">\n			<p>\n				<strong>中国青年网北京12月19日电</strong>（记者 王龙龙 通讯员 闫春旭）“圆圆满满，金榜题名，希望每一个考生都可以圆梦！”2020年全国硕士研究生招生考试在即，为缓解考研学生的压力，河北大学为备考学子送上500份暖心蛋挞，受到了不少考研同学点赞，“非常温暖，感受到了学校的暖意。”\n			</p>\n			<p>\n				<img src=\"https://r.sinaimg.cn/large/tc/edu_youth_cn/44c858a30e6d04ce8db6731993e9c8ac.jpeg\" />&emsp;&emsp;\n			</p>\n			<p>\n				图为河北大学为备考学子送上“暖心蛋挞”。受访者供图\n			</p>\n			<p>\n				&emsp;&emsp;河北大学易班学生工作站的指导老师贾瑞强告诉记者，此次活动是在学生处的指导下，从12月初开始策划的，一共送出500份蛋挞，工作站成员提前统计出学校三个校区考研学生集中的教学楼，并针对这些教学楼进行发放礼品，于17日晚上全部发放完。\n			</p>\n			<p>\n				<img src=\"https://r.sinaimg.cn/large/tc/edu_youth_cn/0f20fa5a2d9e2b529331fe81f215de83.jpeg\" />&emsp;&emsp;\n			</p>\n			<p>\n				图为河北大学为备考学子准备“暖心蛋挞”。受访者供图\n			</p>\n			<p>\n				&emsp;&emsp;“孤军奋战是成熟”“战在一起”“为了远方”“如果成功有捷径，那一定是坚持”“让我们拭目以待”……\n			</p>\n			<p>\n				&emsp;&emsp;记者看到，每一份蛋挞上都附有一张“爱心贴纸”，这些贴纸上都有写有激励考研学子努力拼搏的名言。该工作站站长张悦对记者说，这些“爱心贴纸”都是同学们精心设计的，希望学长学姐们不要有压力，相信天道酬勤，朝着目标院校奋力拼搏。\n			</p>\n			<p>\n				<img src=\"https://r.sinaimg.cn/large/tc/edu_youth_cn/651ab4a3f1b894555084f163f35e8e31.jpeg\" />&emsp;&emsp;\n			</p>\n			<p>\n				图为河北大学校为备考学子准备的“暖心蛋挞”。受访者供图\n			</p>\n			<p>\n				&emsp;&emsp;收到礼物后，很多考研学生感到很暖心，纷纷通过微博微信等网络平台表达感谢。记者在河北大学官方微信上看到不少网友纷纷留言，网友“@艾丽茜娅”留言说：“心里十分温暖，感受到背后有学校和全体河大人的支持。所有考研人，加油！”\n			</p>\n			<p>\n				<img src=\"https://r.sinaimg.cn/large/tc/edu_youth_cn/1b3b377dbfdd106c87d44763d560c36d.jpeg\" />&emsp;&emsp;\n			</p>\n			<p>\n				图为河北大学为备考学子送上“暖心蛋挞”。受访者供图\n			</p>\n			<p>\n				&emsp;&emsp;网友“@小何要好好学习”说：“蛋挞很好吃！一个人在冷风直吹的楼道背书，突然被鼓励关心真的会感动开心好久。”网友“@海有海念”鼓励说：“既然选择了远方，便只顾风雨兼程。考研的学长学姐，加油！”\n			</p>\n			<p>\n				&emsp;&emsp;贾瑞强表示，考研之路漫长而孤独，同学们压力越来越大，希望通过这次送温暖的活动，给予他们鼓励，帮助舒缓压力。“愿同学们都能保持最好的状态，发挥出最好的水平。”\n			</p>\n		</div>\n	</div>\n	<div class=\"DCI_v2 clearfix\" style=\"margin:0px;padding:0px;\">\n	</div>\n</div>\n<div id=\"vplus_payread\" style=\"margin:0px;padding:0px;color:#333333;font-family:Arial, &quot;background-color:#FFFFFF;\">\n</div>\n<div id=\"vplus_shareread_layer\" style=\"margin:0px;padding:0px;color:#333333;font-family:Arial, &quot;background-color:#FFFFFF;\">\n</div>\n<div style=\"margin:0px;padding:0px;color:#333333;font-family:Arial, &quot;background-color:#FFFFFF;\">\n	<div style=\"margin:0px;padding:0px;\">\n		<div class=\"rewardcomponent feed_app_btn_a fend_app_reward\" style=\"margin:0px 0px 20px;padding:0px 0px 40px;text-align:center;\">\n		</div>\n	</div>\n</div>', 'a5fcf4b8-6f3c-4bf8-8e6c-4ec9889ebad7.jpg', '3', '10', '170', '0', '1', '1', '0', '2019-12-19 15:12:46', '2019-12-19 15:12:46', null, '0', null, null, '3');
INSERT INTO `cms_article` VALUES ('25', 'rgfh', 'edgbc nb', '329accae-906d-411a-9911-a34395bb0985.jpg', '9', '30', '170', '0', '0', '-1', '1', '2020-02-06 17:28:19', '2020-02-06 17:28:19', null, '0', null, null, null);
INSERT INTO `cms_article` VALUES ('26', '快讯！厦门防控措施有序有力加码！防控工作架构转为指挥部体系', '<br />\n<br />\n<br />\n<br />\n<span style=\"color:#000000;\">今天上午，省委常委、市委书记胡昌升，市长庄稼汉主持召开厦门市新型冠状病毒感染的肺炎防控工作指挥部会议，按照福建省重大突发公共卫生事件一级响应的预案要求，对全市防控工作进行再部署再落实。</span><br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<p style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;\">\n	<br />\n</p>\n按照一级响应要求，厦门市新型冠状病毒感染的肺炎防控工作组织指挥架构由领导小组转为指挥部体系，胡昌升和庄稼汉任总指挥长，各项预案准备相应升格。会议对防控工作方案和监测措施、隔离措施、救治措施，以及市场供应和监管等工作进行细化研究。<br />\n<img src=\"https://r.sinaimg.cn/large/tc/mmbiz_qpic_cn/c06bed6a48f0285369c2ba9f0c1b0707.jpeg\" /><br />\n会议指出，各级各部门要把疫情防控工作作为当前头等大事，按照一级响应要求，坚决果断、全面周密、系统贯彻一级响应各项规范要求，依法、科学、规范、有序开展防控工作，把防控措施落实到位，切实做到不折不扣、不落不掉。\n<p style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;text-align:center;\">\n	<br />\n</p>\n<br />\n<span style=\"font-size:16px;\"><strong><span style=\"font-family:微软雅黑, Arial, sans-serif;\">会议强调</span></strong></span><br />\n<p style=\"text-align:justify;font-size:14px;\">\n	<br />\n</p>\n必须做到守土有责。按照“外防输入、内防扩散”的工作要求，严防死守，做到守土有责、守土负责、守土履责、守土尽责，建立集中、高效、统一的防控指挥体系，切实形成全市联动、条块结合、联动联防联治的疫情防控工作格局。<br />\n必须做到措施有力。织牢织密防控防线，各级各部门采取的每项措施，都必须管用有效，切实落实早发现、早报告、早隔离、早治疗和集中救治措施，从基层村（居）、街（镇）开始，对来自敏感地区的游客和返乡人员开展彻底摸排，确保不漏一人，并开展体温检测，劝导他们减少不必要的外出和人员接触，做到逢疑必报、逢热必检。严格做好密切接触者追踪排查、隔离观察，查病源、堵源头、严隔离、防扩散，切实阻断传播途径。各级各相关部门要严格落实“日报告”“零报告”和及时报告制度。<br />\n必须强化应急准备。要进一步增加定点医院的隔离病房，对医院设施进行提升改造。加强医疗机构内部防控，关心关爱医务人员，落实医务人员的防护工作，严防医院内部交叉感染，切实保障医务人员健康安全。加快生产、采购、储备防护用品、救治药品、医疗设备、检测试剂等应急物资，确保市场供应和价格稳定。<br />\n<p>\n	<img src=\"https://r.sinaimg.cn/large/tc/mmbiz_qpic_cn/477387fa0247b630cab68049333bf52c.jpeg\" />\n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-size:14px;color:#888888;\">美润股份部分生产线恢复口罩生产。</span>\n</p>\n<br />\n必须加大科学防控知识的宣传和舆论引导。在酒店、机场、火车站、码头、景点等重要部位的醒目位置加强提醒，对防控工作重要信息要通过新闻媒体及时、公开、透明发布，让市民群众及时了解疫情动态和政府采取的防控措施，科学认识疫情，消除恐慌情绪，形成有效防控疫情的良好社会环境。同时，用好“爱心厦门”平台，关心关爱来自敏感地区的人群，为他们提供必要的防护用品和体温检测服务。\n<p style=\"color:#333333;font-family:Arial, &quot;font-size:16px;background-color:#FFFFFF;text-align:center;\">\n	<br />\n</p>\n会议要求，各级各部门要狠抓责任落实，严格落实属地责任，明确部门责任，健全联动机制。要建立疫情防控督查指导制度，加强对摸排检测、疫情报告、医疗救治等工作的督促检查，把各项防控措施落实到工作第一线。坚决反对形式主义、官僚主义，对防控工作行动迟缓、措施不力造成疫情扩散等严重后果的单位和个人严肃追责问责。<br />\n市领导黄强、黄文辉、国桂荣出席会议。', '567f0a1f-d9c3-4ecc-87c7-68f8ba67b1f4.jpg', '3', '10', '170', '0', '1', '1', '0', '2020-02-06 17:30:32', '2020-02-06 17:30:32', null, '0', null, null, null);
